import React, { useState } from 'react';
import { Switch, Route, BrowserRouter as Router } from 'react-router-dom';
import Home from './components/home';
import PremiumContent from './components/premium-content';
import Login from './components/login';
import Register from './components/register';
import Profile from './components/profile';
import Callback from './components/callback';
import Payment from './components/payment';
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import PaymentForm from "./components/payment-form";
//import "./App.css";

// Make sure to call loadStripe outside of a component’s render to avoid
// recreating the Stripe object on every render.
// loadStripe is initialized with your real test publishable API key.
const promise = loadStripe("pk_test_51IGbZJBgidwKCIjAJJvUAqtr1UGzMKd8GKPNirXjlIn60lgGFn1dxy2ZcRHFvl1qPlELvylycn5QxlbVzyZCsEdm00s91euGQq");

function App() {
  const [lifetimeAccess, setLifetimeAccess] = useState(false);
  const [lifetimeAccessRequestStatus, setLifetimeAccessRequestStatus] = useState("");
  console.log('lifetimezzz', lifetimeAccess)
  return (
    <Router>
      <Switch>
        <Route path='/' exact component={Home} />
        <Route path='/premium-content'
          render={() => {
            return (
              <PremiumContent
                lifetimeAccess={lifetimeAccess}
                setLifetimeAccess={setLifetimeAccess}
                lifetimeAccessRequestStatus={lifetimeAccessRequestStatus}
                setLifetimeAccessRequestStatus={setLifetimeAccessRequestStatus}
              />
            )
          }}
        />
        <Route path='/register' component={Register} />
        <Route path='/login' component={Login} />
        <Route path='/profile' component={Profile} />
        <Route path='/callback' component={Callback} />
        <Route path='/payment'
        render={(props) => {
          return (<Payment 
          Elements={Elements} 
          PaymentForm={PaymentForm} 
          promise={promise}
          />)
        }}
        />
      </Switch>
    </Router>
  );
}

export default App;
