import { useUser } from '../lib/hooks';
import Layout from './layout';

const Home = () => {
  const user = useUser();

  return (
    <Layout>
      {user ? <div>Now that the user has created an account and subscribed to a recurring payment, they'll be able to see all of your <b>PREMIUM</b> newsletters here!</div> : <div>All of your <b>FREE</b> newsletters can be displayed here.</div>}
      <style>{`
        div {
          font-size: 17px;
        }
      `}</style>
    </Layout>
  );
};

export default Home;
