import React, { useContext, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { UserContext } from "../lib/UserContext";
import Loading from "./loading";
import { CallToAction } from "@magiclabs/ui";

const PremiumContent = ({
  setLifetimeAccess,
  lifetimeAccess,
  setLifetimeAccessRequestStatus,
  lifetimeAccessRequestStatus,
}) => {
  const [user] = useContext(UserContext);
  const history = useHistory();

  // If not loading and no user found, redirect to /login
  useEffect(() => {
    user && !user.loading && !user.issuer && history.push("/login");
  }, [user, history]);

  // Get the user's email
  const email = user?.loading ? <Loading /> : user?.email;

  // Check to see whether or not the user has paid.
  useEffect(() => {
    if (email && !lifetimeAccessRequestStatus) {
      setLifetimeAccessRequestStatus("Pending");
      window
        .fetch(`${process.env.REACT_APP_SERVER_URL}/validate-customer`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ email }),
        })
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setLifetimeAccessRequestStatus("Complete");
          if (
            data.customer.length &&
            data.customer[0].metadata.lifetimeAccess === "true"
          ) {
            setLifetimeAccess(true);
          }
        });
    }
  });

  return (
    <Layout>
      {
        // Display Premium Content if the user's logged in & have already paid for lifetime access
        user && lifetimeAccess ? (
          <>
            <h3 className="h3-header">Here's all of our PREMIUM content! üòç</h3>
            <div>‚ô° PREMIUM AWESOMENESS.</div>
            <div>‚ô° PREMIUM AWESOMENESS.</div>
            <div>‚ô° PREMIUM AWESOMENESS.</div>
            <div>‚ô° PREMIUM AWESOMENESS.</div>
          </>
        ) : (
          <>
            <h3 className="h3-header">So you want our PREMIUM content? üòé</h3>
            <div>üß† Our premium content includes some AWESOME stuff.</div>

            <div>
              ü™Ñ To access the awesomeness, purchase a Lifetime Access Pass
              below.
            </div>

            <div>üí∏ The Magic Lifetime Access Pass is only $50!</div>
            {
              // If the user is logged in, go straight to payment
              user ? (
                <CallToAction
                  color="primary"
                  size="sm"
                  onPress={() => history.push("/payment")}
                >
                  Count Me In
                </CallToAction>
              ) : (
                // Otherwise, ask the user to sign up first
                <CallToAction
                  color="primary"
                  size="sm"
                  onPress={() => history.push("/signup")}
                >
                  Count Me In
                </CallToAction>
              )
            }
          </>
        )
      }
      <style>{`
        .h3-header {
          font-size: 22px;
          margin: 25px 0;
        }
        div {
          font-size: 17px;
          margin-bottom: 15px;
        }
      `}</style>
    </Layout>
  );
};

export default PremiumContent;
