import Layout from "./layout";
import { useUser } from "../lib/hooks";

export default function Payment({ Elements, PaymentForm, promise }) {
  const user = useUser();
  const person = user && user.email ? user.email : "";

  return (
    <div>
      <Layout>
        <h3 className="h3-header">Purchase Lifetime Access Pass</h3>
        Welcome {person}üôãüèª‚Äç‚ôÄÔ∏è! You successfully signed up with your email. Please enter
        your card information below to purchase your Lifetime Access Pass
        securely via Stripe:
        {user ? (
          <Elements stripe={promise}>
            <PaymentForm email={user.email} />
          </Elements>
        ) : (
          "Waiting for user info..."
        )}
      </Layout>
      <style>{`
        form,
        p {
          margin-bottom: 15px;
        }
        label {
          display: flex;
          flex-flow: column;
          text-align: center;
        }
        .h3-header {
          font-size: 22px;
          margin: 25px 0;
        }
        .input-wrapper {
          width: 87%;
          margin: 0 auto;
        }
        .submit {
          display: flex;
          justify-content: space-between;
          width: 87%;
          margin: 20px auto 0;
        }
      `}</style>
    </div>
  );
}
