import React, { useState, useEffect } from "react";
import { useUser } from "../lib/hooks";
import Layout from "./layout";
import { CallToAction } from "@magiclabs/ui";
import { useHistory } from "react-router-dom";

const PremiumContent = ({
  setLifetimeAccess,
  lifetimeAccess,
  setLifetimeAccessRequestStatus,
  lifetimeAccessRequestStatus,
}) => {
  const user = useUser();
  console.log("user: ", user);
  const email = user && user.email ? user.email : "";
  const history = useHistory();

  console.log("email: ", email);

  // Check to see whether or not the user has paid.
  useEffect(() => {
    console.log("test 1");
    console.log("lifetimeAccessRequestStatus", lifetimeAccessRequestStatus);
    if (email && !lifetimeAccessRequestStatus) {
      console.log("test 2");
      setLifetimeAccessRequestStatus("Pending");
      window
        .fetch("http://localhost:8080/validate-customer", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ email }),
        })
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setLifetimeAccessRequestStatus("Complete");
          console.log("data.customer.length: ", data.customer.length);
          // console.log('data.customer[0].metadata.lifetimeAccess: ', data.customer[0].metadata.lifetimeAccess)
          console.log("data from validating customer: ", data);
          if (
            data.customer.length &&
            data.customer[0].metadata.lifetimeAccess === "true"
          ) {
            console.log("hi do i get in");
            setLifetimeAccess(true);
          }
        });
    }
  });

  console.log("lifetimeAccess: ", lifetimeAccess);

  return (
    <Layout>
      {user && lifetimeAccess ? (
        <div>Here are all of our PREMIUM content!</div>
      ) : (
        <div>
          <div>
            Our premium content includes video tutorials on building
            Decentralized Applications.
          </div>

          <div>
            To access PREMIUM content, purchase a Lifetime Access Pass below.
          </div>

          <div>The Magic Lifetime Access Pass is only $50.</div>
          {user ? (
            <CallToAction
              color="primary"
              size="sm"
              onPress={() => history.push("/payment")}
            >
              Purchase
            </CallToAction>
          ) : (
            <CallToAction
              color="primary"
              size="sm"
              onPress={() => history.push("/signup")}
            >
              Purchase
            </CallToAction>
          )}
        </div>
      )}
      <style>{`
        div {
          font-size: 17px;
        }
      `}</style>
    </Layout>
  );
};

export default PremiumContent;
