/**
 * Creates an `Error` with a consistent message format.
 */
export function createError(label: string, message: string): Error;
export function createError(component: React.FC<any>, message: string): Error;
export function createError(source: string | React.FC<any>, message: string): Error {
  const label = typeof source === 'string' ? source : source.displayName;
  return new Error(`[${label}] ${message}`);
}
