import { createTheme } from './create-theme';

export type ThemeType = 'auto' | 'dark' | 'light';

export interface Theme extends ReturnType<typeof createTheme> {}

export type BaseColorName =
  | 'primary'
  | 'secondary'
  | 'tertiary'
  | 'success'
  | 'warning'
  | 'error'
  | 'light'
  | 'dark'
  | 'mid';

export type ColorTone = 'base' | 'lighter' | 'lightest' | 'darker' | 'darkest';

export type Breakpoint = 'xs' | 'sm' | 'md' | 'lg' | 'xl';
export type BreakpointConfig<T = number> = Record<Breakpoint, T>;
