import React from 'react';
import { StoryMetadata, Story } from '../../types/storybook-metadata';
import { Icon, MonochromeIcons } from '../../components/icons';
import { Outset, FlexRow } from '../../components/layout';
import { useTheme } from '../../components/theme/use-theme';

export default {
  title: 'Icons/Monochrome Icons',
  component: Icon,
} as StoryMetadata;

export const IconStory: Story = () => {
  const theme = useTheme();

  return (
    <FlexRow wrap>
      <Outset wrapItemsWith="div" all={20}>
        {Object.entries(MonochromeIcons).map(([key, iconDef]) => {
          return <Icon size={50} color={theme.hex.primary.base} type={iconDef} key={key} />;
        })}
      </Outset>
    </FlexRow>
  );
};

IconStory.argTypes = {};

IconStory.storyName = 'Monochrome Icons';
