import React, { useState } from 'react';
import { StoryMetadata, Story } from '../../types/storybook-metadata';
import { Tooltip } from '../../components/feedback';

export default {
  title: 'Feedback/Tooltip',
  component: Tooltip,
} as StoryMetadata;

export const SimpleTooltip: Story = () => {
  const [show, setShow] = useState(true);

  return (
    <div style={{ height: '2000px', marginTop: '250px' }} onClick={() => setShow(!show)}>
      <Tooltip
        placement="top"
        anchor={<div style={{ height: '250px', padding: '20px', border: 'solid 1px black' }}>I'm an anchor!</div>}
        in={show}
        delay={0}
      >
        <div>I'm a tooltip!</div>
      </Tooltip>
    </div>
  );
};
